import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';

class EventList extends Component {
  constructor(){
    super()
    this.state={
        listaEventos:[1, 2, 3]
    }
    this.handleLoad = this.handleLoad.bind(this)
}

 handleLoad() {
    axios.post('http://localhost:4000/app/getEventList', {usuario: localStorage.getItem('usuarioLogado')})
        .then(response => {
          console.log(response.data[0].descricao)
          return response.data
        })
  }

render(){
  return (
    <div className="createUserPage">
        <div className="inner-form">
            <h2>Lista de eventos</h2>
              <div className="events-div">
                    <ul>
                        <li>1</li>
                        <li>2</li>
                        <li>3</li>
                        <li>4</li>
                    </ul>
                    <p>{this.listaEventos[0].descricao}</p>
              </div>
        </div>

      <Link style={{textDecoration:"none"}} to='/'><button className="back-btn">Voltar</button></Link>
    </div>
  );
}
}

export default EventList;
