import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';

class EventList extends Component {
  constructor(){
    super()
    this.state={
        list: ['a', 'b', 'c', 'd'],
        lista:[{"dataFim":"", "dataInicio":"", "descricao":"", "usuario":""}]
    };
    this.handleLoad = this.handleLoad.bind(this)
    this.formatDataString = this.formatDataString.bind(this)
}

 handleLoad() {
    axios.post('http://localhost:4000/app/getEventList', {usuario: localStorage.getItem('usuarioLogado')})
        .then(response => {
          this.setState({ lista: response.data });
          console.log(this.state.lista)
        })
  }

  componentDidMount() {
    this.handleLoad();
  }

  formatDataString(dataString){
    const d = new Date(dataString)

    return d.getDate()+"/"+(d.getMonth()+1)+"/"+d.getFullYear()+" - "+d.getUTCHours()+":"+(d.getMinutes()<10?'0':'') + d.getMinutes()
}

render(){
  return (
    <div className="createUserPage">
        <div className="inner-form">
            <h2>Lista de eventos</h2>
              <div className="events-div">
              <table>
                <thead>
                <tr>
                    <th>Descrição</th>
                    <th>Data Início</th>
                    <th>Data Término</th>
                    <th>Editar</th>
                    <th>Deletar</th>
                </tr>
                </thead>
                <tbody>
                    {
                        this.state.lista.map((item, index) => (
                            <tr key={index}>
                                <td>{item.descricao}</td>
                                <td>{this.formatDataString(item.dataInicio)}</td>
                                <td>{this.formatDataString(item.dataFim)}</td>
                                <td>ed</td>
                                <td>del</td>
                                <td/>
                            </tr>
                        ))
                    }
                </tbody>
            </table>
                    <p> xxxxxxxxxxxxxxxxxx</p>
                    <ul>
                        <li>1</li>
                        <li>2</li>
                        <li>3</li>
                        <li>4</li>
                    </ul>
                        <p> new</p>
                    <p>x</p>
                    <p>{this.state.lista[0].dataInicio}</p>
                    <p> xxxxxxxxxxxxxxxxxx</p>
              </div>
        </div>

      <Link style={{textDecoration:"none"}} to='/'><button className="back-btn">Voltar</button></Link>
    </div>
  );
}
}

export default EventList;
