  render(){
  return (
    <BrowserRouter>
        <div className="App">
          <div className="page-header">
              <h1>Sistema de Calendário</h1>
              <h1>{this.state.usuarioLogado.username}   - {this.state.usuarioLogado.password}</h1>
              <button id="logout-btn" onClick={logout}>Sair</button>

          </div>
            <Switch>
            <Route path='/' exact> <Home /> </Route>
            <Route path='/criarUsuario'> <CreateUser /> </Route>
            <Route path="/login"> <Login /> </Route>
          </Switch>
        </div>
    </BrowserRouter>


  );
 }
}

