import React, {useState} from 'react';
import Login from './components/Login';
import CreateUser from './components/CreateUser';
import Home from './components/Home';
import Menu from './components/Menu';
import {BrowserRouter, Switch, Route, Link} from 'react-router-dom';

function App() {
  const adminUser = {
    username: "admin",
    password: "admin"
  }

  const [user, setUser] = useState({username: ""});

  const LoginCheck = dados => {
    if(dados.username==adminUser.username && dados.password==adminUser.password){
      setUser({
        username:dados.username,
      });
    }
    else
    {
      alert("Usuário ou Senha Incorretos.");
    }
  }

  const Logout = () => {
    setUser({name: ""});
    window.location.reload(false);
  }

  return (
    <BrowserRouter>
        <div className="App">
          <div className="page-header">
              <h1>Sistema de Calendário</h1>
          </div>
          <div className="userLoginMsg">{(user.username!="") ? (<div> <h4>Usuário Logado: {user.username}</h4>
            <button onClick={Logout} id="logout-btn">Sair</button>
            <Menu />
            </div>

          ) : (<Switch>
            <Route path='/' exact> <Home /> </Route>
            <Route path='/criarUsuario'> <CreateUser /> </Route>
            <Route path="/login"> <Login Login={LoginCheck}/> </Route>
          </Switch>)}</div>
        </div>
    </BrowserRouter>


  );
}

export default App;
