import React, {Components} from 'react';
import {Link} from 'react-router-dom';

class Login extends Components {
    

  Render() {
    return (
    <div className="createUserPage">
      <form onSubmit={submitHandler}>
        <div className="inner-form">
            <h2>Realizar Login</h2>
              <div className="form-group">
                  <label htmlFor="nome">Nome de Usuário:</label>
                  <input type="text" name="nome" id="nome" onChange={e => setDados({...dados, username: e.target.value})} value={dados.username}/>
              </div>
              <div className="form-group">
                  <label htmlFor="senha">Senha:</label>
                  <input type="password" name="senha" id="senha" onChange={e => setDados({...dados, password: e.target.value})} value={dados.password}/>
              </div>
              <input type="submit" value="Entrar" />
        </div>
      </form>

      <Link style={{textDecoration:"none"}} to='/'><button className="back-btn">Voltar</button></Link>
    </div>

    )
  }
}

export default Login;
