import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';

class CreateEvent extends Component {
  constructor(){
    super()
    this.state ={
        descricao:'',
        dataInicio:'',
        dataFim:'',
        usuario:''
    }
